import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Button,
  Grid,
  Card,
  CardContent,
  Chip,
  Alert,
  Avatar,
  Tabs,
  Tab,
  Divider,
} from '@mui/material';
import {
  ArrowBack,
  Edit,
  Delete,
  Email,
  Phone,
  LocationOn,
  CalendarToday,
  Info,
  AccountCircle,
} from '@mui/icons-material';
import { useNavigate, useParams } from 'react-router-dom';
import { toast } from 'react-toastify';
import { userAPI } from '../services/api';
import { UserCardSkeleton } from '../components/SkeletonLoader';

const UserDetails = () => {
  const navigate = useNavigate();
  const { id } = useParams();

  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [tab, setTab] = useState(0);

  useEffect(() => {
    fetchUser();
  }, [id]); // eslint-disable-line react-hooks/exhaustive-deps

  const fetchUser = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await userAPI.getUser(id);
      setUser(response.data.data);
    } catch (err) {
      setError(err.message);
      toast.error('Failed to fetch user details');
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = () => navigate(`/users/${id}/edit`);
  const handleDelete = async () => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      try {
        await userAPI.deleteUser(id);
        toast.success('User deleted successfully');
        navigate('/users');
      } catch (err) {
        toast.error('Failed to delete user');
      }
    }
  };
  const handleBack = () => navigate('/users');

  const formatDate = (dateString) =>
    new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    });

  if (loading) return <UserCardSkeleton />;

  if (error) {
    return (
      <Box>
        <Button startIcon={<ArrowBack />} onClick={handleBack} sx={{ mb: 2 }}>
          Back to Users
        </Button>
        <Alert severity="error">{error}</Alert>
      </Box>
    );
  }

  if (!user) {
    return (
      <Box>
        <Button startIcon={<ArrowBack />} onClick={handleBack} sx={{ mb: 2 }}>
          Back to Users
        </Button>
        <Alert severity="warning">User not found</Alert>
      </Box>
    );
  }

  return (
    <Box>
      {/* Back button */}
      <Button
        startIcon={<ArrowBack />}
        onClick={handleBack}
        variant="outlined"
        sx={{ mb: 3 }}
      >
        Back to Users
      </Button>

      {/* Profile Header */}
      <Card sx={{ mb: 3, borderRadius: 3, boxShadow: 2 }}>
        <CardContent>
          <Grid container alignItems="center" spacing={2}>
            <Grid item>
              <Avatar
                sx={{ width: 80, height: 80, fontSize: '2rem' }}
              >
                {user.name.charAt(0).toUpperCase()}
              </Avatar>
            </Grid>
            <Grid item xs>
              <Typography variant="h5" fontWeight="bold">
                {user.name}
              </Typography>
              <Typography variant="body1" color="text.secondary">
                {user.email}
              </Typography>
              <Chip
                label="Active"
                color="success"
                size="small"
                sx={{ mt: 1 }}
              />
            </Grid>
            <Grid item>
              <Button
                variant="contained"
                startIcon={<Edit />}
                onClick={handleEdit}
                sx={{ mr: 1 }}
              >
                Edit
              </Button>
              <Button
                variant="outlined"
                color="error"
                startIcon={<Delete />}
                onClick={handleDelete}
              >
                Delete
              </Button>
            </Grid>
          </Grid>
        </CardContent>
      </Card>

      {/* Tabs */}
      <Card sx={{ borderRadius: 3, boxShadow: 1 }}>
        <Tabs
          value={tab}
          onChange={(_, newValue) => setTab(newValue)}
          variant="fullWidth"
          aria-label="User detail tabs"
          sx={{
            borderBottom: 1,
            borderColor: 'divider',
            '& .MuiTab-root': { fontWeight: 600 },
          }}
        >
          <Tab icon={<Info />} label="Overview" />
          <Tab icon={<Email />} label="Contact" />
          <Tab icon={<AccountCircle />} label="Account" />
        </Tabs>

        <CardContent>
          {tab === 0 && (
            <Box>
              <Typography variant="h6" gutterBottom>
                Quick Overview
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {user.name} has been active since {formatDate(user.createdAt)}.  
                Profile was last updated on {formatDate(user.updatedAt)}.
              </Typography>
            </Box>
          )}

          {tab === 1 && (
            <Grid container spacing={2}>
              <Grid item xs={12} md={6}>
                <Card sx={{ p: 2, borderRadius: 2 }}>
                  <Box display="flex" alignItems="center" gap={2}>
                    <Email color="primary" />
                    <Box>
                      <Typography variant="caption">Email</Typography>
                      <Typography variant="body1">{user.email}</Typography>
                    </Box>
                  </Box>
                </Card>
              </Grid>

              <Grid item xs={12} md={6}>
                <Card sx={{ p: 2, borderRadius: 2 }}>
                  <Box display="flex" alignItems="center" gap={2}>
                    <Phone color="secondary" />
                    <Box>
                      <Typography variant="caption">Phone</Typography>
                      <Typography variant="body1">{user.phone || 'N/A'}</Typography>
                    </Box>
                  </Box>
                </Card>
              </Grid>

              <Grid item xs={12}>
                <Card sx={{ p: 2, borderRadius: 2 }}>
                  <Box display="flex" alignItems="center" gap={2}>
                    <LocationOn color="success" />
                    <Box>
                      <Typography variant="caption">Address</Typography>
                      <Typography variant="body1">{user.address || 'N/A'}</Typography>
                    </Box>
                  </Box>
                </Card>
              </Grid>
            </Grid>
          )}

          {tab === 2 && (
            <Grid container spacing={2}>
              <Grid item xs={12} md={6}>
                <Card sx={{ p: 2, borderRadius: 2 }}>
                  <Box display="flex" alignItems="center" gap={2}>
                    <CalendarToday color="warning" />
                    <Box>
                      <Typography variant="caption">Created At</Typography>
                      <Typography variant="body1">{formatDate(user.createdAt)}</Typography>
                    </Box>
                  </Box>
                </Card>
              </Grid>
              <Grid item xs={12} md={6}>
                <Card sx={{ p: 2, borderRadius: 2 }}>
                  <Box display="flex" alignItems="center" gap={2}>
                    <CalendarToday color="info" />
                    <Box>
                      <Typography variant="caption">Last Updated</Typography>
                      <Typography variant="body1">{formatDate(user.updatedAt)}</Typography>
                    </Box>
                  </Box>
                </Card>
              </Grid>
            </Grid>
          )}
        </CardContent>
      </Card>
    </Box>
  );
};

export default UserDetails;
