import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Button,
  Grid,
  Card,
  CardContent,
  Chip,
  Alert,
  useTheme,
  Avatar,
  Divider,
  Tooltip,
  Tabs,
  Tab,
} from '@mui/material';
import {
  ArrowBack,
  Edit,
  Delete,
  Email,
  Phone,
  LocationOn,
  CalendarToday,
  Info,
  AccountCircle,
} from '@mui/icons-material';
import { useNavigate, useParams } from 'react-router-dom';
import { toast } from 'react-toastify';
import { userAPI } from '../services/api';
import { UserCardSkeleton } from '../components/SkeletonLoader';

const UserDetails = () => {
  const navigate = useNavigate();
  const { id } = useParams();
  const theme = useTheme();

  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [tab, setTab] = useState(0);

  useEffect(() => {
    fetchUser();
  }, [id]); // eslint-disable-line react-hooks/exhaustive-deps

  const fetchUser = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await userAPI.getUser(id);
      setUser(response.data.data);
    } catch (err) {
      setError(err.message);
      toast.error('Failed to fetch user details');
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = () => navigate(`/users/${id}/edit`);

  const handleDelete = async () => {
    if (window.confirm('Are you sure you want to delete this user? This action cannot be undone.')) {
      try {
        await userAPI.deleteUser(id);
        toast.success('User deleted successfully');
        navigate('/users');
      } catch (err) {
        toast.error('Failed to delete user');
      }
    }
  };

  const handleBack = () => navigate('/users');

  const formatDate = (dateString) =>
    new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });

  if (loading) return <UserCardSkeleton />;

  if (error) {
    return (
      <Box>
        <Button startIcon={<ArrowBack />} onClick={handleBack} sx={{ mb: 2 }}>
          Back to Users
        </Button>
        <Alert severity="error">{error}</Alert>
      </Box>
    );
  }

  if (!user) {
    return (
      <Box>
        <Button startIcon={<ArrowBack />} onClick={handleBack} sx={{ mb: 2 }}>
          Back to Users
        </Button>
        <Alert severity="warning">User not found</Alert>
      </Box>
    );
  }

  return (
    <Box>
      {/* Back button */}
      <Button
        startIcon={<ArrowBack />}
        onClick={handleBack}
        variant="outlined"
        sx={{ mb: 3 }}
      >
        Back to Users
      </Button>

      {/* Profile Banner */}
      <Card sx={{ p: 3, mb: 4, borderRadius: 3, boxShadow: 3 }}>
        <Box display="flex" alignItems="center" justifyContent="space-between" flexWrap="wrap" gap={2}>
          {/* Left: Avatar + Name */}
          <Box display="flex" alignItems="center" gap={3}>
            <Avatar
              sx={{
                width: 100,
                height: 100,
                bgcolor: theme.palette.primary.main,
                fontSize: '2.5rem',
                fontWeight: 'bold',
              }}
              aria-label="User avatar"
            >
              {user.name.charAt(0).toUpperCase()}
            </Avatar>
            <Box>
              <Typography variant="h4" fontWeight="bold">{user.name}</Typography>
              <Typography variant="body1" color="text.secondary">{user.email}</Typography>
              <Chip
                label="Active User"
                color="success"
                size="small"
                sx={{ mt: 1 }}
                aria-label="User status: active"
              />
            </Box>
          </Box>

          {/* Right: Actions */}
          <Box display="flex" gap={1}>
            <Tooltip title="Edit User">
              <Button variant="contained" startIcon={<Edit />} onClick={handleEdit}>
                Edit
              </Button>
            </Tooltip>
            <Tooltip title="Delete User">
              <Button variant="outlined" color="error" startIcon={<Delete />} onClick={handleDelete}>
                Delete
              </Button>
            </Tooltip>
          </Box>
        </Box>
      </Card>

      {/* Tabs */}
      <Card sx={{ borderRadius: 3, boxShadow: 2 }}>
        <Tabs
          value={tab}
          onChange={(_, newValue) => setTab(newValue)}
          variant="scrollable"
          scrollButtons
          allowScrollButtonsMobile
          aria-label="User details navigation tabs"
          sx={{
            borderBottom: 1,
            borderColor: 'divider',
            '& .MuiTab-root': { fontWeight: 600, minHeight: 64 },
          }}
        >
          <Tab icon={<Info />} iconPosition="start" label="Overview" />
          <Tab icon={<Email />} iconPosition="start" label="Contact" />
          <Tab icon={<AccountCircle />} iconPosition="start" label="Account" />
        </Tabs>

        {/* Tab Content */}
        <CardContent sx={{ p: 3 }}>
          {/* Overview */}
          {tab === 0 && (
            <Box>
              <Typography variant="h6" gutterBottom fontWeight="bold">
                Quick Overview
              </Typography>
              <Typography variant="body1" color="text.secondary">
                {user.name} has been an active member since {formatDate(user.createdAt)}. 
                Last profile update was on {formatDate(user.updatedAt)}.
              </Typography>
            </Box>
          )}

          {/* Contact Info */}
          {tab === 1 && (
            <Grid container spacing={3}>
              <Grid item xs={12} md={6}>
                <Box display="flex" alignItems="center" gap={2}>
                  <Email color="primary" fontSize="large" />
                  <Box>
                    <Typography variant="body2" color="text.secondary">Email</Typography>
                    <Typography variant="body1" fontWeight="600">{user.email}</Typography>
                  </Box>
                </Box>
              </Grid>

              <Grid item xs={12} md={6}>
                <Box display="flex" alignItems="center" gap={2}>
                  <Phone color="secondary" fontSize="large" />
                  <Box>
                    <Typography variant="body2" color="text.secondary">Phone</Typography>
                    <Typography variant="body1" fontWeight="600">{user.phone || 'N/A'}</Typography>
                  </Box>
                </Box>
              </Grid>

              <Grid item xs={12}>
                <Box display="flex" alignItems="flex-start" gap={2}>
                  <LocationOn color="success" fontSize="large" />
                  <Box>
                    <Typography variant="body2" color="text.secondary">Address</Typography>
                    <Typography variant="body1" fontWeight="600">{user.address || 'N/A'}</Typography>
                  </Box>
                </Box>
              </Grid>
            </Grid>
          )}

          {/* Account Info */}
          {tab === 2 && (
            <Grid container spacing={3}>
              <Grid item xs={12} md={6}>
                <Box display="flex" alignItems="center" gap={2}>
                  <CalendarToday color="warning" fontSize="large" />
                  <Box>
                    <Typography variant="body2" color="text.secondary">Created At</Typography>
                    <Typography variant="body1" fontWeight="600">{formatDate(user.createdAt)}</Typography>
                  </Box>
                </Box>
              </Grid>
              <Grid item xs={12} md={6}>
                <Box display="flex" alignItems="center" gap={2}>
                  <CalendarToday color="info" fontSize="large" />
                  <Box>
                    <Typography variant="body2" color="text.secondary">Last Updated</Typography>
                    <Typography variant="body1" fontWeight="600">{formatDate(user.updatedAt)}</Typography>
                  </Box>
                </Box>
              </Grid>
            </Grid>
          )}
        </CardContent>
      </Card>
    </Box>
  );
};

export default UserDetails;
