import React, { useState, useEffect } from 'react';
import {
  Box,
  Paper,
  Typography,
  Grid,
  Card,
  CardContent,
  CardActions,
  IconButton,
  Avatar,
  Button,
  Tooltip,
  TextField,
  InputAdornment,
  Fade,
  Chip,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Alert,
  useTheme,
  useMediaQuery
} from '@mui/material';
import {
  Search,
  Add,
  Edit,
  Delete,
  Visibility,
  FileDownload,
  Refresh,
  Group,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import { userAPI } from '../services/api';
import SkeletonLoader from '../components/SkeletonLoader';

const UserList = () => {
  const navigate = useNavigate();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [totalUsers, setTotalUsers] = useState(0);
  const [deleteDialog, setDeleteDialog] = useState({ open: false, user: null });

  const fetchUsers = async (search = searchTerm) => {
    try {
      setLoading(true);
      setError(null);
      const response = await userAPI.getUsers(1, 100, search); // fetch more at once
      const { users: userData, pagination } = response.data.data;
      setUsers(userData);
      setTotalUsers(pagination.totalUsers);
    } catch (err) {
      setError(err.message);
      toast.error('Failed to fetch users');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []); // only first render

  const handleSearch = (event) => {
    const value = event.target.value;
    setSearchTerm(value);
    const timeoutId = setTimeout(() => {
      fetchUsers(value);
    }, 400);
    return () => clearTimeout(timeoutId);
  };

  const handleDelete = async (userId) => {
    try {
      await userAPI.deleteUser(userId);
      toast.success('User deleted successfully');
      setDeleteDialog({ open: false, user: null });
      fetchUsers();
    } catch (err) {
      toast.error('Failed to delete user');
    }
  };

  const handleExport = async () => {
    try {
      const response = await userAPI.exportUsers();
      const blob = new Blob([response.data], { type: 'text/csv' });
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `users_${new Date().toISOString().split('T')[0]}.csv`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);
      toast.success('Users exported successfully');
    } catch (err) {
      toast.error('Failed to export users');
    }
  };

  if (loading && users.length === 0) {
    return (
      <Box>
        <SkeletonLoader rows={2} columns={isMobile ? 1 : 3} />
      </Box>
    );
  }

  return (
    <Fade in timeout={600}>
      <Box>
        {/* Header */}
        <Paper
          sx={{
            p: 3,
            mb: 3,
            background: 'linear-gradient(135deg, #4f46e5, #9333ea)',
            color: 'white',
            borderRadius: 3,
          }}
        >
          <Box display="flex" flexDirection="column" gap={2}>
            <Typography variant="h4" fontWeight="bold">
              User Management
            </Typography>
            <Typography variant="body1" sx={{ opacity: 0.9 }}>
              Your one-stop solution to manage your users
            </Typography>
            <Box display="flex" gap={2} alignItems="center" flexWrap="wrap">
              <Chip
                icon={<Group />}
                label={`${totalUsers} Users`}
                sx={{ backgroundColor: 'white', color: 'black', fontWeight: 600 }}
              />
              <Button
                variant="contained"
                startIcon={<Add />}
                onClick={() => navigate('/users/new')}
                sx={{ backgroundColor: 'white', color: '#4f46e5', fontWeight: 600 }}
              >
                Add User
              </Button>
              <Button variant="outlined" startIcon={<Refresh />} onClick={() => fetchUsers()}>
                Refresh
              </Button>
              <Button
                variant="outlined"
                startIcon={<FileDownload />}
                onClick={handleExport}
                disabled={users.length === 0}
              >
                Export CSV
              </Button>
            </Box>
            <TextField
              placeholder="Search users..."
              value={searchTerm}
              onChange={handleSearch}
              fullWidth
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <Search sx={{ color: 'white' }} />
                  </InputAdornment>
                ),
              }}
              sx={{
                backgroundColor: 'rgba(255,255,255,0.1)',
                borderRadius: 2,
                input: { color: 'white' },
                '& .MuiOutlinedInput-notchedOutline': { border: 'none' },
              }}
            />
          </Box>
        </Paper>

        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}

        {/* User Grid */}
        <Grid container spacing={3}>
          {users.length === 0 ? (
            <Grid item xs={12}>
              <Paper sx={{ p: 5, textAlign: 'center' }}>
                <Typography variant="h6" color="text.secondary">
                  {searchTerm ? 'No users found for this search.' : 'No users yet. Add your first user!'}
                </Typography>
                {!searchTerm && (
                  <Button
                    variant="contained"
                    startIcon={<Add />}
                    sx={{ mt: 2 }}
                    onClick={() => navigate('/users/new')}
                  >
                    Add User
                  </Button>
                )}
              </Paper>
            </Grid>
          ) : (
            users.map((user, index) => (
              <Grid item xs={12} sm={6} md={4} key={user._id}>
                <Fade in timeout={400 + index * 100}>
                  <Card
                    sx={{
                      borderRadius: 3,
                      boxShadow: 4,
                      '&:hover': { transform: 'scale(1.02)', transition: '0.2s' },
                    }}
                  >
                    <CardContent>
                      <Box display="flex" alignItems="center" gap={2} mb={2}>
                        <Avatar sx={{ bgcolor: theme.palette.primary.main }}>
                          {user.name.charAt(0).toUpperCase()}
                        </Avatar>
                        <Box>
                          <Typography variant="h6">{user.name}</Typography>
                          <Typography variant="body2" color="text.secondary">
                            {user.email}
                          </Typography>
                        </Box>
                      </Box>
                      <Typography variant="body2">📞 {user.phone || '—'}</Typography>
                      <Typography variant="body2" noWrap>
                        📍 {user.address || '—'}
                      </Typography>
                    </CardContent>
                    <CardActions sx={{ justifyContent: 'flex-end' }}>
                      <Tooltip title="View">
                        <IconButton onClick={() => navigate(`/users/${user._id}`)}>
                          <Visibility />
                        </IconButton>
                      </Tooltip>
                      <Tooltip title="Edit">
                        <IconButton onClick={() => navigate(`/users/${user._id}/edit`)}>
                          <Edit />
                        </IconButton>
                      </Tooltip>
                      <Tooltip title="Delete">
                        <IconButton
                          color="error"
                          onClick={() => setDeleteDialog({ open: true, user })}
                        >
                          <Delete />
                        </IconButton>
                      </Tooltip>
                    </CardActions>
                  </Card>
                </Fade>
              </Grid>
            ))
          )}
        </Grid>

        {/* Delete Confirmation */}
        <Dialog
          open={deleteDialog.open}
          onClose={() => setDeleteDialog({ open: false, user: null })}
        >
          <DialogTitle>Confirm Delete</DialogTitle>
          <DialogContent>
            <Typography>
              Are you sure you want to delete user "{deleteDialog.user?.name}"?
            </Typography>
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setDeleteDialog({ open: false, user: null })}>
              Cancel
            </Button>
            <Button
              color="error"
              variant="contained"
              onClick={() => handleDelete(deleteDialog.user._id)}
            >
              Delete
            </Button>
          </DialogActions>
        </Dialog>
      </Box>
    </Fade>
  );
};

export default UserList;
